import 'package:hive/hive.dart';
import 'package:todo_pro/core/constants/enums.dart';

part 'task_model.g.dart';

@HiveType(typeId: 0)
class TaskModel {
  @HiveField(0)
  final String id;

  @HiveField(1)
  String title;

  @HiveField(2)
  String? description;

  @HiveField(3)
  DateTime dueDate;

  @HiveField(4)
  TaskPriority priority;

  @HiveField(5)
  TaskStatus status;

  @HiveField(6)
  String category;

  @HiveField(7)
  bool hasReminder;

  @HiveField(8)
  DateTime createdAt;

  @HiveField(9)
  DateTime updatedAt;

  @HiveField(10)
  List<String> tags;

  TaskModel({
    required this.id,
    required this.title,
    this.description,
    required this.dueDate,
    required this.priority,
    required this.status,
    required this.category,
    this.hasReminder = false,
    required this.createdAt,
    required this.updatedAt,
    this.tags = const [],
  });

  factory TaskModel.create({
    required String title,
    String? description,
    required DateTime dueDate,
    TaskPriority priority = TaskPriority.medium,
    String category = 'Personal',
    bool hasReminder = false,
    List<String> tags = const [],
  }) {
    final now = DateTime.now();
    return TaskModel(
      id: DateTime.now().millisecondsSinceEpoch.toString(),
      title: title,
      description: description,
      dueDate: dueDate,
      priority: priority,
      status: TaskStatus.pending,
      category: category,
      hasReminder: hasReminder,
      createdAt: now,
      updatedAt: now,
      tags: tags,
    );
  }

  TaskModel copyWith({
    String? title,
    String? description,
    DateTime? dueDate,
    TaskPriority? priority,
    TaskStatus? status,
    String? category,
    bool? hasReminder,
    List<String>? tags,
  }) {
    return TaskModel(
      id: id,
      title: title ?? this.title,
      description: description ?? this.description,
      dueDate: dueDate ?? this.dueDate,
      priority: priority ?? this.priority,
      status: status ?? this.status,
      category: category ?? this.category,
      hasReminder: hasReminder ?? this.hasReminder,
      createdAt: createdAt,
      updatedAt: DateTime.now(),
      tags: tags ?? this.tags,
    );
  }

  bool get isOverdue => dueDate.isBefore(DateTime.now()) && status != TaskStatus.completed;

  bool get isDueToday => DateUtils.isToday(dueDate);

  @override
  String toString() {
    return 'Task(id: $id, title: $title, status: $status, dueDate: $dueDate)';
  }
}

repositories.task_repository.dart:
import 'package:hive/hive.dart';
import 'package:todo_pro/data/models/task_model.dart';
import 'package:todo_pro/core/error/failure.dart';

abstract class TaskRepository {
  Future<List<TaskModel>> getTasks();
  Future<TaskModel> getTask(String id);
  Future<void> addTask(TaskModel task);
  Future<void> updateTask(TaskModel task);
  Future<void> deleteTask(String id);
  Future<void> deleteAllTasks();
}

class TaskRepositoryImpl implements TaskRepository {
  final Box<TaskModel> tasksBox;

  TaskRepositoryImpl(this.tasksBox);

  @override
  Future<List<TaskModel>> getTasks() async {
    try {
      return tasksBox.values.toList();
    } catch (e) {
      throw DatabaseFailure('Failed to load tasks: $e');
    }
  }

  @override
  Future<TaskModel> getTask(String id) async {
    try {
      final task = tasksBox.get(id);
      if (task == null) {
        throw DatabaseFailure('Task not found');
      }
      return task;
    } catch (e) {
      throw DatabaseFailure('Failed to get task: $e');
    }
  }

  @override
  Future<void> addTask(TaskModel task) async {
    try {
      await tasksBox.put(task.id, task);
    } catch (e) {
      throw DatabaseFailure('Failed to add task: $e');
    }
  }

  @override
  Future<void> updateTask(TaskModel task) async {
    try {
      await tasksBox.put(task.id, task);
    } catch (e) {
      throw DatabaseFailure('Failed to update task: $e');
    }
  }

  @override
  Future<void> deleteTask(String id) async {
    try {
      await tasksBox.delete(id);
    } catch (e) {
      throw DatabaseFailure('Failed to delete task: $e');
    }
  }

  @override
  Future<void> deleteAllTasks() async {
    try {
      await tasksBox.clear();
    } catch (e) {
      throw DatabaseFailure('Failed to delete all tasks: $e');
    }
  }
}

