providers.task_provider.dart:
import 'package:flutter/foundation.dart';
import 'package:hive/hive.dart';
import 'package:todo_pro/data/models/task_model.dart';
import 'package:todo_pro/data/repositories/task_repository.dart';
import 'package:todo_pro/core/constants/enums.dart';

class TaskProvider extends ChangeNotifier {
  final TaskRepository _repository;
  List<TaskModel> _tasks = [];
  SortType _sortType = SortType.date;
  FilterType _filterType = FilterType.all;
  bool _isLoading = false;
  String? _errorMessage;

  TaskProvider() : _repository = TaskRepositoryImpl(Hive.box<TaskModel>('tasks')) {
    _loadTasks();
  }

  List<TaskModel> get tasks => _applyFilterAndSort(_tasks);
  SortType get sortType => _sortType;
  FilterType get filterType => _filterType;
  bool get isLoading => _isLoading;
  String? get errorMessage => _errorMessage;

  int get totalTasks => _tasks.length;
  int get pendingTasks => _tasks.where((t) => t.status == TaskStatus.pending).length;
  int get completedTasks => _tasks.where((t) => t.status == TaskStatus.completed).length;

  Future<void> _loadTasks() async {
    _isLoading = true;
    _errorMessage = null;
    notifyListeners();

    try {
      _tasks = await _repository.getTasks();
    } catch (e) {
      _errorMessage = 'Failed to load tasks: $e';
    } finally {
      _isLoading = false;
      notifyListeners();
    }
  }

  Future<void> addTask(TaskModel task) async {
    try {
      await _repository.addTask(task);
      await _loadTasks();
    } catch (e) {
      _errorMessage = 'Failed to add task: $e';
      notifyListeners();
    }
  }

  Future<void> updateTask(TaskModel task) async {
    try {
      await _repository.updateTask(task);
      await _loadTasks();
    } catch (e) {
      _errorMessage = 'Failed to update task: $e';
      notifyListeners();
    }
  }

  Future<void> deleteTask(String taskId) async {
    try {
      await _repository.deleteTask(taskId);
      await _loadTasks();
    } catch (e) {
      _errorMessage = 'Failed to delete task: $e';
      notifyListeners();
    }
  }

  Future<void> deleteAllTasks() async {
    try {
      await _repository.deleteAllTasks();
      await _loadTasks();
    } catch (e) {
      _errorMessage = 'Failed to delete all tasks: $e';
      notifyListeners();
    }
  }

  void setSortType(SortType type) {
    _sortType = type;
    notifyListeners();
  }

  void setFilterType(FilterType type) {
    _filterType = type;
    notifyListeners();
  }

  List<TaskModel> _applyFilterAndSort(List<TaskModel> tasks) {
    final filtered = _applyFilter(tasks);
    return _applySort(filtered);
  }

  List<TaskModel> _applyFilter(List<TaskModel> tasks) {
    switch (_filterType) {
      case FilterType.today:
        return tasks.where((task) => task.isDueToday).toList();
      case FilterType.upcoming:
        return tasks.where((task) => task.dueDate.isAfter(DateTime.now())).toList();
      case FilterType.pending:
        return tasks.where((task) => task.status == TaskStatus.pending).toList();
      case FilterType.completed:
        return tasks.where((task) => task.status == TaskStatus.completed).toList();
      case FilterType.all:
      default:
        return tasks;
    }
  }

  List<TaskModel> _applySort(List<TaskModel> tasks) {
    switch (_sortType) {
      case SortType.priority:
        return List.from(tasks)
          ..sort((a, b) => b.priority.index.compareTo(a.priority.index));
      case SortType.title:
        return List.from(tasks)
          ..sort((a, b) => a.title.compareTo(b.title));
      case SortType.category:
        return List.from(tasks)
          ..sort((a, b) => a.category.compareTo(b.category));
      case SortType.date:
      default:
        return List.from(tasks)
          ..sort((a, b) => a.dueDate.compareTo(b.dueDate));
    }
  }
}

common.app_button.dart:
import 'package:flutter/material.dart';

class AppButton extends StatelessWidget {
  final String text;
  final VoidCallback onPressed;
  final Color? backgroundColor;
  final Color? textColor;
  final bool isLoading;
  final bool expanded;
  final IconData? icon;

  const AppButton({
    super.key,
    required this.text,
    required this.onPressed,
    this.backgroundColor,
    this.textColor,
    this.isLoading = false,
    this.expanded = false,
    this.icon,
  });

  @override
  Widget build(BuildContext context) {
    final theme = Theme.of(context);

    Widget buttonChild = isLoading
        ? const SizedBox(
            height: 24,
            width: 24,
            child: CircularProgressIndicator(strokeWidth: 2),
          )
        : Row(
            mainAxisSize: MainAxisSize.min,
            children: [
              if (icon != null) ...[
                Icon(icon, size: 20),
                const SizedBox(width: 8),
              ],
              Text(
                text,
                style: theme.textTheme.labelLarge?.copyWith(
                  color: textColor ?? theme.colorScheme.onPrimary,
                  fontWeight: FontWeight.w600,
                ),
              ),
            ],
          );

    final button = ElevatedButton(
      onPressed: isLoading ? null : onPressed,
      style: ElevatedButton.styleFrom(
        backgroundColor: backgroundColor ?? theme.colorScheme.primary,
        foregroundColor: textColor ?? theme.colorScheme.onPrimary,
        padding: const EdgeInsets.symmetric(horizontal: 24, vertical: 16),
        shape: RoundedRectangleBorder(
          borderRadius: BorderRadius.circular(12),
        ),
      ),
      child: buttonChild,
    );

    if (expanded) {
      return SizedBox(
        width: double.infinity,
        child: button,
      );
    }

    return button;
  }
}

