import 'package:flutter/material.dart';
import 'package:flutter_slidable/flutter_slidable.dart';
import 'package:todo_pro/data/models/task_model.dart';
import 'package:todo_pro/core/utils/extensions.dart';
import 'package:todo_pro/core/utils/date_utils.dart';

class TaskCard extends StatelessWidget {
  final TaskModel task;
  final VoidCallback onTap;
  final VoidCallback onDelete;
  final VoidCallback onToggleStatus;

  const TaskCard({
    super.key,
    required this.task,
    required this.onTap,
    required this.onDelete,
    required this.onToggleStatus,
  });

  @override
  Widget build(BuildContext context) {
    final theme = Theme.of(context);

    return Padding(
      padding: const EdgeInsets.symmetric(horizontal: 16, vertical: 4),
      child: Slidable(
        endActionPane: ActionPane(
          motion: const ScrollMotion(),
          children: [
            SlidableAction(
              onPressed: (_) => onToggleStatus(),
              backgroundColor: theme.colorScheme.secondary,
              foregroundColor: theme.colorScheme.onSecondary,
              icon: task.status == TaskStatus.completed
                  ? Icons.undo
                  : Icons.check,
              label: task.status == TaskStatus.completed
                  ? 'Undo'
                  : 'Complete',
            ),
            SlidableAction(
              onPressed: (_) => onDelete(),
              backgroundColor: Colors.red,
              foregroundColor: Colors.white,
              icon: Icons.delete,
              label: 'Delete',
            ),
          ],
        ),
        child: Card(
          elevation: 2,
          child: ListTile(
            onTap: onTap,
            leading: CircleAvatar(
              backgroundColor: task.priority.color.withOpacity(0.1),
              child: Icon(
                task.status.icon,
                color: task.priority.color,
                size: 20,
              ),
            ),
            title: Text(
              task.title,
              style: theme.textTheme.titleMedium?.copyWith(
                decoration: task.status == TaskStatus.completed
                    ? TextDecoration.lineThrough
                    : null,
                color: task.status == TaskStatus.completed
                    ? theme.colorScheme.onSurface.withOpacity(0.5)
                    : theme.colorScheme.onSurface,
              ),
            ),
            subtitle: Column(
              crossAxisAlignment: CrossAxisAlignment.start,
              children: [
                if (task.description != null && task.description!.isNotEmpty)
                  Padding(
                    padding: const EdgeInsets.only(bottom: 4),
                    child: Text(
                      task.description!,
                      maxLines: 1,
                      overflow: TextOverflow.ellipsis,
                      style: theme.textTheme.bodySmall?.copyWith(
                        color: theme.colorScheme.onSurface.withOpacity(0.6),
                      ),
                    ),
                  ),
                Wrap(
                  spacing: 8,
                  children: [
                    Chip(
                      label: Text(task.category),
                      visualDensity: VisualDensity.compact,
                      backgroundColor: theme.colorScheme.secondary.withOpacity(0.1),
                    ),
                    Chip(
                      label: Text(task.priority.name),
                      visualDensity: VisualDensity.compact,
                      backgroundColor: task.priority.color.withOpacity(0.1),
                      labelStyle: TextStyle(color: task.priority.color),
                    ),
                  ],
                ),
              ],
            ),
            trailing: Column(
              mainAxisAlignment: MainAxisAlignment.center,
              crossAxisAlignment: CrossAxisAlignment.end,
              children: [
                Text(
                  DateUtils.formatDate(task.dueDate),
                  style: theme.textTheme.bodySmall?.copyWith(
                    color: theme.colorScheme.onSurface.withOpacity(0.6),
                    fontWeight: FontWeight.w500,
                  ),
                ),
                if (task.isOverdue)
                  Text(
                    'Overdue',
                    style: theme.textTheme.bodySmall?.copyWith(
                      color: Colors.red,
                    ),
                  ),
              ],
            ),
          ),
        ),
      ),
    );
  }
}

