import 'package:flutter/material.dart';
import 'package:provider/provider.dart';
import 'package:todo_pro/presentation/providers/task_provider.dart';
import 'package:todo_pro/presentation/widgets/task/task_card.dart';
import 'package:todo_pro/presentation/widgets/task/task_stats_card.dart';
import 'package:todo_pro/presentation/widgets/common/empty_state.dart';
import 'package:todo_pro/presentation/widgets/common/loading_indicator.dart';
import 'package:todo_pro/core/constants/enums.dart';

class HomeScreen extends StatefulWidget {
  const HomeScreen({super.key});

  @override
  State<HomeScreen> createState() => _HomeScreenState();
}

class _HomeScreenState extends State<HomeScreen> {
  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        title: const Text('Todo Pro'),
        actions: [
          PopupMenuButton<SortType>(
            onSelected: (type) {
              context.read<TaskProvider>().setSortType(type);
            },
            itemBuilder: (context) => [
              const PopupMenuItem(
                value: SortType.date,
                child: Text('Sort by Date'),
              ),
              const PopupMenuItem(
                value: SortType.priority,
                child: Text('Sort by Priority'),
              ),
              const PopupMenuItem(
                value: SortType.title,
                child: Text('Sort by Title'),
              ),
              const PopupMenuItem(
                value: SortType.category,
                child: Text('Sort by Category'),
              ),
            ],
          ),
          PopupMenuButton<FilterType>(
            onSelected: (type) {
              context.read<TaskProvider>().setFilterType(type);
            },
            itemBuilder: (context) => [
              const PopupMenuItem(
                value: FilterType.all,
                child: Text('All Tasks'),
              ),
              const PopupMenuItem(
                value: FilterType.today,
                child: Text('Due Today'),
              ),
              const PopupMenuItem(
                value: FilterType.upcoming,
                child: Text('Upcoming'),
              ),
              const PopupMenuItem(
                value: FilterType.pending,
                child: Text('Pending'),
              ),
              const PopupMenuItem(
                value: FilterType.completed,
                child: Text('Completed'),
              ),
            ],
          ),
        ],
      ),
      body: Consumer<TaskProvider>(
        builder: (context, provider, _) {
          if (provider.isLoading) {
            return const LoadingIndicator(message: 'Loading tasks...');
          }

          if (provider.errorMessage != null) {
            return Center(
              child: Column(
                mainAxisAlignment: MainAxisAlignment.center,
                children: [
                  const Icon(Icons.error_outline, size: 64, color: Colors.red),
                  const SizedBox(height: 16),
                  Text(
                    'Error',
                    style: Theme.of(context).textTheme.titleLarge,
                  ),
                  const SizedBox(height: 8),
                  Padding(
                    padding: const EdgeInsets.symmetric(horizontal: 32),
                    child: Text(
                      provider.errorMessage!,
                      textAlign: TextAlign.center,
                      style: Theme.of(context).textTheme.bodyMedium,
                    ),
                  ),
                  const SizedBox(height: 16),
                  ElevatedButton(
                    onPressed: () => provider._loadTasks(),
                    child: const Text('Retry'),
                  ),
                ],
              ),
            );
          }

          final tasks = provider.tasks;

          if (tasks.isEmpty) {
            return EmptyState(
              icon: Icons.task_outlined,
              title: 'No Tasks',
              message: provider.filterType == FilterType.all
                  ? 'Tap the + button to create your first task!'
                  : 'No tasks found with the current filter',
              buttonText: provider.filterType != FilterType.all
                  ? 'Show All Tasks'
                  : null,
              onButtonPressed: provider.filterType != FilterType.all
                  ? () => provider.setFilterType(FilterType.all)
                  : null,
            );
          }

          return RefreshIndicator(
            onRefresh: () => provider._loadTasks(),
            child: ListView(
              children: [
                const TaskStatsCard(),
                const SizedBox(height: 16),
                ...tasks.map((task) => TaskCard(
                  task: task,
                  onTap: () => _navigateToEditTask(context, task),
                  onDelete: () => _showDeleteDialog(context, task),
                  onToggleStatus: () => _toggleTaskStatus(provider, task),
                )),
              ],
            ),
          );
        },
      ),
      floatingActionButton: FloatingActionButton(
        onPressed: () => _navigateToAddTask(context),
        child: const Icon(Icons.add),
      ),
    );
  }

  void _navigateToAddTask(BuildContext context) {
    Navigator.pushNamed(context, '/add_task');
  }

  void _navigateToEditTask(BuildContext context, TaskModel task) {
    Navigator.pushNamed(context, '/edit_task', arguments: task);
  }

  void _showDeleteDialog(BuildContext context, TaskModel task) {
    showDialog(
      context: context,
      builder: (context) => AlertDialog(
        title: const Text('Delete Task'),
        content: Text('Are you sure you want to delete "${task.title}"?'),
        actions: [
          TextButton(
            onPressed: () => Navigator.pop(context),
            child: const Text('Cancel'),
          ),
          TextButton(
            onPressed: () {
              context.read<TaskProvider>().deleteTask(task.id);
              Navigator.pop(context);
            },
            child: const Text('Delete', style: TextStyle(color: Colors.red)),
          ),
        ],
      ),
    );
  }

  void _toggleTaskStatus(TaskProvider provider, TaskModel task) {
    final newStatus = task.status == TaskStatus.completed
        ? TaskStatus.pending
        : TaskStatus.completed;

    provider.updateTask(task.copyWith(status: newStatus));
  }
}

