import 'package:flutter/material.dart';
import 'package:flutter_riverpod/flutter_riverpod.dart';
import 'package:supashop_multivendor/presentation/screens/auth/auth_screen.dart';
import 'package:supashop_multivendor/presentation/screens/buyer/buyer_home_screen.dart';
import 'package:supashop_multivendor/presentation/screens/seller/seller_dashboard_screen.dart';
import 'package:supashop_multivendor/presentation/screens/admin/admin_dashboard_screen.dart';
import 'package:supashop_multivendor/core/providers/auth_provider.dart';

class App extends ConsumerWidget {
  const App({super.key});

  @override
  Widget build(BuildContext context, WidgetRef ref) {
    final authState = ref.watch(authProvider);

    return authState.when(
      data: (user) {
        if (user == null) {
          return const AuthScreen();
        }

        // Route based on user role
        switch (user.role) {
          case UserRole.buyer:
            return const BuyerHomeScreen();
          case UserRole.seller:
            return const SellerDashboardScreen();
          case UserRole.admin:
            return const AdminDashboardScreen();
          default:
            return const AuthScreen();
        }
      },
      loading: () => const Scaffold(
        body: Center(
          child: CircularProgressIndicator(),
        ),
      ),
      error: (error, stack) => Scaffold(
        body: Center(
          child: Text('Error: $error'),
        ),
      ),
    );
  }
}

