supabase.supabase_client.dart:
import 'package:supabase_flutter/supabase_flutter.dart';
import 'package:supashop_multivendor/core/constants/app_constants.dart';

class SupabaseClient {
  static late final SupabaseClient _instance;
  static Supabase get client => Supabase.instance;

  static Future<void> initialize() async {
    await Supabase.initialize(
      url: AppConstants.supabaseUrl,
      anonKey: AppConstants.supabaseAnonKey,
      authCallbackUrlHostname: 'login-callback', // For deep linking
      debug: true, // Enable in development
    );
  }

  // Auth methods
  static User? get currentUser => client.auth.currentUser;

  static Future<AuthResponse> signUp({
    required String email,
    required String password,
    required Map<String, dynamic> userData,
  }) async {
    return await client.auth.signUp(
      email: email,
      password: password,
      data: userData,
    );
  }

  static Future<AuthResponse> signInWithEmail({
    required String email,
    required String password,
  }) async {
    return await client.auth.signInWithPassword(
      email: email,
      password: password,
    );
  }

  static Future<void> signOut() async {
    await client.auth.signOut();
  }

  static Future<void> resetPassword(String email) async {
    await client.auth.resetPasswordForEmail(email);
  }

  // Database methods
  static PostgrestFilterBuilder<dynamic> from(String table) {
    return client.from(table);
  }

  // Storage methods
  static SupabaseStorageClient get storage => client.storage;

  static Future<String> uploadFile({
    required String bucket,
    required String path,
    required String fileName,
    required File file,
  }) async {
    final response = await storage
        .from(bucket)
        .uploadBinary(
          '$path/$fileName',
          await file.readAsBytes(),
          fileOptions: const FileOptions(
            upsert: true,
            contentType: 'image/jpeg',
          ),
        );

    return storage.from(bucket).getPublicUrl('$path/$fileName');
  }

  static Future<void> deleteFile({
    required String bucket,
    required String path,
  }) async {
    await storage.from(bucket).remove([path]);
  }

  // Realtime subscriptions
  static RealtimeChannel channel(String name) {
    return client.channel(name);
  }
}

