import 'package:equatable/equatable.dart';
import 'package:supashop_multivendor/core/constants/enums.dart';

class ProductModel extends Equatable {
  final String id;
  final String shopId;
  final String? categoryId;
  final String title;
  final String slug;
  final String? description;
  final double price;
  final double? comparePrice;
  final double? cost;
  final String? sku;
  final String? barcode;
  final int quantity;
  final double? weight;
  final String weightUnit;
  final String condition;
  final List<String> images;
  final Map<String, dynamic>? attributes;
  final List<String> tags;
  final double rating;
  final int totalRatings;
  final int totalSales;
  final ProductStatus status;
  final bool isFeatured;
  final DateTime createdAt;
  final DateTime updatedAt;

  const ProductModel({
    required this.id,
    required this.shopId,
    this.categoryId,
    required this.title,
    required this.slug,
    this.description,
    required this.price,
    this.comparePrice,
    this.cost,
    this.sku,
    this.barcode,
    required this.quantity,
    this.weight,
    this.weightUnit = 'kg',
    this.condition = 'New',
    this.images = const [],
    this.attributes,
    this.tags = const [],
    this.rating = 0,
    this.totalRatings = 0,
    this.totalSales = 0,
    this.status = ProductStatus.active,
    this.isFeatured = false,
    required this.createdAt,
    required this.updatedAt,
  });

  factory ProductModel.fromJson(Map<String, dynamic> json) {
    return ProductModel(
      id: json['id'],
      shopId: json['shop_id'],
      categoryId: json['category_id'],
      title: json['title'],
      slug: json['slug'],
      description: json['description'],
      price: (json['price'] as num).toDouble(),
      comparePrice: json['compare_price'] != null
          ? (json['compare_price'] as num).toDouble()
          : null,
      cost: json['cost'] != null ? (json['cost'] as num).toDouble() : null,
      sku: json['sku'],
      barcode: json['barcode'],
      quantity: json['quantity'] ?? 0,
      weight: json['weight'] != null ? (json['weight'] as num).toDouble() : null,
      weightUnit: json['weight_unit'] ?? 'kg',
      condition: json['condition'] ?? 'New',
      images: List<String>.from(json['images'] ?? []),
      attributes: json['attributes'],
      tags: List<String>.from(json['tags'] ?? []),
      rating: (json['rating'] as num?)?.toDouble() ?? 0,
      totalRatings: json['total_ratings'] ?? 0,
      totalSales: json['total_sales'] ?? 0,
      status: ProductStatus.values.firstWhere(
        (e) => e.name == json['status'],
        orElse: () => ProductStatus.active,
      ),
      isFeatured: json['is_featured'] ?? false,
      createdAt: DateTime.parse(json['created_at']),
      updatedAt: DateTime.parse(json['updated_at']),
    );
  }

  double get discountPercent {
    if (comparePrice == null || comparePrice! <= price) return 0;
    return ((comparePrice! - price) / comparePrice!) * 100;
  }

  bool get isOutOfStock => quantity <= 0;
  bool get hasDiscount => discountPercent > 0;

  @override
  List<Object?> get props => [id, title, slug];
}

providers.auth_provider.dart:
import 'package:flutter_riverpod/flutter_riverpod.dart';
import 'package:supashop_multivendor/core/supabase/supabase_client.dart';
import 'package:supashop_multivendor/core/models/user_model.dart';

final authProvider = StreamProvider<UserModel?>((ref) async* {
  // Listen to auth state changes
  final authChanges = SupabaseClient.client.auth.onAuthStateChange;

  await for (final event in authChanges) {
    final user = event.session?.user;

    if (user == null) {
      yield null;
      continue;
    }

    // Get user profile from database
    try {
      final response = await SupabaseClient.from('users')
          .select()
          .eq('id', user.id)
          .single();

      yield UserModel.fromJson(response);
    } catch (e) {
      // Create user profile if doesn't exist
      final newUser = UserModel(
        id: user.id,
        email: user.email!,
        fullName: user.userMetadata?['full_name'],
        avatarUrl: user.userMetadata?['avatar_url'],
        role: UserRole.buyer,
        createdAt: DateTime.now(),
      );

      await SupabaseClient.from('users').insert(newUser.toJson());
      yield newUser;
    }
  }
});

providers.cart_provider.dart:
import 'package:flutter_riverpod/flutter_riverpod.dart';
import 'package:supashop_multivendor/core/supabase/supabase_client.dart';
import 'package:supashop_multivendor/core/models/product_model.dart';

class CartItem {
  final String id;
  final ProductModel product;
  final int quantity;
  final Map<String, dynamic>? selectedVariants;

  CartItem({
    required this.id,
    required this.product,
    this.quantity = 1,
    this.selectedVariants,
  });

  double get totalPrice => product.price * quantity;
}

final cartProvider = StateNotifierProvider<CartNotifier, List<CartItem>>((ref) {
  return CartNotifier();
});

class CartNotifier extends StateNotifier<List<CartItem>> {
  CartNotifier() : super([]) {
    _loadCart();
  }

  Future<void> _loadCart() async {
    final user = SupabaseClient.currentUser;
    if (user == null) return;

    try {
      final response = await SupabaseClient.from('cart_items')
          .select('*, product:products(*)')
          .eq('user_id', user.id);

      final items = response.map((item) {
        return CartItem(
          id: item['id'],
          product: ProductModel.fromJson(item['product']),
          quantity: item['quantity'],
          selectedVariants: item['selected_variants'],
        );
      }).toList();

      state = items;
    } catch (e) {
      print('Error loading cart: $e');
    }
  }

  Future<void> addToCart({
    required ProductModel product,
    int quantity = 1,
    Map<String, dynamic>? variants,
  }) async {
    final user = SupabaseClient.currentUser;
    if (user == null) throw Exception('User not logged in');

    try {
      await SupabaseClient.from('cart_items').upsert({
        'user_id': user.id,
        'product_id': product.id,
        'quantity': quantity,
        'selected_variants': variants,
      });

      await _loadCart();
    } catch (e) {
      throw Exception('Failed to add to cart: $e');
    }
  }

  Future<void> updateQuantity(String itemId, int newQuantity) async {
    if (newQuantity <= 0) {
      await removeFromCart(itemId);
      return;
    }

    try {
      await SupabaseClient.from('cart_items')
          .update({'quantity': newQuantity})
          .eq('id', itemId);

      await _loadCart();
    } catch (e) {
      throw Exception('Failed to update quantity: $e');
    }
  }

  Future<void> removeFromCart(String itemId) async {
    try {
      await SupabaseClient.from('cart_items').delete().eq('id', itemId);
      await _loadCart();
    } catch (e) {
      throw Exception('Failed to remove from cart: $e');
    }
  }

  Future<void> clearCart() async {
    final user = SupabaseClient.currentUser;
    if (user == null) return;

    try {
      await SupabaseClient.from('cart_items')
          .delete()
          .eq('user_id', user.id);

      state = [];
    } catch (e) {
      throw Exception('Failed to clear cart: $e');
    }
  }

  double get subtotal => state.fold(
    0,
    (total, item) => total + item.totalPrice,
  );

  int get itemCount => state.fold(
    0,
    (count, item) => count + item.quantity,
  );
}

