  IconData _getActivityIcon(String type) {
    switch (type) {
      case 'new_user':
        return Icons.person_add;
      case 'new_order':
        return Icons.shopping_bag;
      case 'new_shop':
        return Icons.store;
      case 'new_review':
        return Icons.star;
      default:
        return Icons.notifications;
    }
  }

  Color _getActivityColor(String type) {
    switch (type) {
      case 'new_user':
        return Colors.blue;
      case 'new_order':
        return Colors.green;
      case 'new_shop':
        return Colors.orange;
      case 'new_review':
        return Colors.amber;
      default:
        return Colors.grey;
    }
  }
}

