import 'package:hive/hive.dart';
import 'package:supabase_flutter/supabase_flutter.dart';
import 'package:supabase_fashion/core/constants/enums.dart';

part 'supabase_product_model.g.dart';

@HiveType(typeId: 1)
class SupabaseProductModel {
  @HiveField(0)
  final String id;

  @HiveField(1)
  final String name;

  @HiveField(2)
  final String slug;

  @HiveField(3)
  final String description;

  @HiveField(4)
  final double price;

  @HiveField(5)
  final double? discountPrice;

  @HiveField(6)
  final List<String> images;

  @HiveField(7)
  final List<String> colors;

  @HiveField(8)
  final List<String> sizes;

  @HiveField(9)
  final String categoryId;

  @HiveField(10)
  final String? brandId;

  @HiveField(11)
  final double rating;

  @HiveField(12)
  final int reviewCount;

  @HiveField(13)
  final int stock;

  @HiveField(14)
  final bool isFeatured;

  @HiveField(15)
  final bool isNew;

  @HiveField(16)
  final DateTime createdAt;

  @HiveField(17)
  final DateTime updatedAt;

  SupabaseProductModel({
    required this.id,
    required this.name,
    required this.slug,
    required this.description,
    required this.price,
    this.discountPrice,
    required this.images,
    required this.colors,
    required this.sizes,
    required this.categoryId,
    this.brandId,
    this.rating = 0.0,
    this.reviewCount = 0,
    required this.stock,
    this.isFeatured = false,
    this.isNew = false,
    required this.createdAt,
    required this.updatedAt,
  });

  factory SupabaseProductModel.fromJson(Map<String, dynamic> json) {
    return SupabaseProductModel(
      id: json['id'],
      name: json['name'],
      slug: json['slug'],
      description: json['description'] ?? '',
      price: (json['price'] as num).toDouble(),
      discountPrice: json['discount_price'] != null
          ? (json['discount_price'] as num).toDouble()
          : null,
      images: List<String>.from(json['images'] ?? []),
      colors: List<String>.from(json['colors'] ?? []),
      sizes: List<String>.from(json['sizes'] ?? []),
      categoryId: json['category_id'],
      brandId: json['brand_id'],
      rating: (json['rating'] as num?)?.toDouble() ?? 0.0,
      reviewCount: json['review_count'] ?? 0,
      stock: json['stock'] ?? 0,
      isFeatured: json['is_featured'] ?? false,
      isNew: json['is_new'] ?? false,
      createdAt: DateTime.parse(json['created_at']),
      updatedAt: DateTime.parse(json['updated_at']),
    );
  }

  factory SupabaseProductModel.fromPostgrest(PostgrestMap json) {
    return SupabaseProductModel.fromJson(json);
  }

  Map<String, dynamic> toJson() {
    return {
      'id': id,
      'name': name,
      'slug': slug,
      'description': description,
      'price': price,
      'discount_price': discountPrice,
      'images': images,
      'colors': colors,
      'sizes': sizes,
      'category_id': categoryId,
      'brand_id': brandId,
      'rating': rating,
      'review_count': reviewCount,
      'stock': stock,
      'is_featured': isFeatured,
      'is_new': isNew,
      'created_at': createdAt.toIso8601String(),
      'updated_at': updatedAt.toIso8601String(),
    };
  }

  String get firstImage => images.isNotEmpty ? images.first : '';

  double get discountedPrice => discountPrice ?? price;

  double get discountPercentage {
    if (discountPrice == null) return 0;
    return ((price - discountPrice!) / price * 100).roundToDouble();
  }

  bool get hasDiscount => discountPrice != null && discountPrice! < price;

  bool get isOutOfStock => stock <= 0;

  String get formattedPrice => '\\\$price.toStringAsFixed(2)';

  String get formattedDiscountedPrice => '\\\$discountedPrice.toStringAsFixed(2)';
}

repositories.supabase_product_repository.dart:
import 'package:supabase_flutter/supabase_flutter.dart';
import 'package:supabase_fashion/data/models/supabase_product_model.dart';
import 'package:supabase_fashion/core/constants/supabase_constants.dart';
import 'package:supabase_fashion/core/error/failure.dart';

typedef ProductQuery = Map<String, dynamic>;

typedef ProductFilter = ({String? categoryId, double? minPrice, double? maxPrice, List<String>? colors, List<String>? sizes});

abstract class SupabaseProductRepository {
  Future<List<SupabaseProductModel>> getProducts({
    int limit,
    int offset,
    ProductFilter? filter,
    String? searchQuery,
    String? sortBy,
  });

  Stream<List<SupabaseProductModel>> watchProducts({
    ProductFilter? filter,
    String? searchQuery,
  });

  Future<SupabaseProductModel> getProduct(String id);

  Future<List<SupabaseProductModel>> getProductsByIds(List<String> ids);

  Future<List<SupabaseProductModel>> getFeaturedProducts();

  Future<List<SupabaseProductModel>> getRelatedProducts(String productId);

  Future<void> updateProductStock(String productId, int quantity);
}

class SupabaseProductRepositoryImpl implements SupabaseProductRepository {
  final SupabaseClient _supabase;

  SupabaseProductRepositoryImpl(this._supabase);

  @override
  Future<List<SupabaseProductModel>> getProducts({
    int limit = 20,
    int offset = 0,
    ProductFilter? filter,
    String? searchQuery,
    String? sortBy,
  }) async {
    try {
      var query = _supabase
          .from(SupabaseConstants.productsTable)
          .select('*')
          .range(offset, offset + limit - 1);

      // Apply filters
      if (filter != null) {
        if (filter.categoryId != null) {
          query = query.eq('category_id', filter.categoryId);
        }
        if (filter.minPrice != null) {
          query = query.gte('price', filter.minPrice);
        }
        if (filter.maxPrice != null) {
          query = query.lte('price', filter.maxPrice);
        }
        if (filter.colors != null && filter.colors!.isNotEmpty) {
          query = query.contains('colors', filter.colors!);
        }
        if (filter.sizes != null && filter.sizes!.isNotEmpty) {
          query = query.contains('sizes', filter.sizes!);
        }
      }

      // Apply search
      if (searchQuery != null && searchQuery.isNotEmpty) {
        query = query.ilike('name', '%\\$searchQuery%');
      }

      // Apply sorting
      if (sortBy != null) {
        switch (sortBy) {
          case 'price_asc':
            query = query.order('price');
            break;
          case 'price_desc':
            query = query.order('price', ascending: false);
            break;
          case 'rating':
            query = query.order('rating', ascending: false);
            break;
          case 'newest':
            query = query.order('created_at', ascending: false);
            break;
          default:
            query = query.order('created_at', ascending: false);
        }
      } else {
        query = query.order('created_at', ascending: false);
      }

      final response = await query;

      return response
          .map((json) => SupabaseProductModel.fromJson(json))
          .toList();
    } catch (e) {
      throw DatabaseFailure('Failed to fetch products: \\$e');
    }
  }

  @override
  Stream<List<SupabaseProductModel>> watchProducts({
    ProductFilter? filter,
    String? searchQuery,
  }) {
    return _supabase
        .from(SupabaseConstants.productsTable)
        .stream(primaryKey: ['id'])
        .map((event) => event
            .map((json) => SupabaseProductModel.fromJson(json))
            .toList());
  }

  @override
  Future<SupabaseProductModel> getProduct(String id) async {
    try {
      final response = await _supabase
          .from(SupabaseConstants.productsTable)
          .select('*')
          .eq('id', id)
          .single();

      return SupabaseProductModel.fromJson(response);
    } catch (e) {
      throw DatabaseFailure('Failed to fetch product: \\$e');
    }
  }

  @override
  Future<List<SupabaseProductModel>> getProductsByIds(List<String> ids) async {
    try {
      if (ids.isEmpty) return [];

      final response = await _supabase
          .from(SupabaseConstants.productsTable)
          .select('*')
          .in_('id', ids);

      return response
          .map((json) => SupabaseProductModel.fromJson(json))
          .toList();
    } catch (e) {
      throw DatabaseFailure('Failed to fetch products by ids: \\$e');
    }
  }

  @override
  Future<List<SupabaseProductModel>> getFeaturedProducts() async {
    try {
      final response = await _supabase
          .from(SupabaseConstants.productsTable)
          .select('*')
          .eq('is_featured', true)
          .order('created_at', ascending: false)
          .limit(10);

      return response
          .map((json) => SupabaseProductModel.fromJson(json))
          .toList();
    } catch (e) {
      throw DatabaseFailure('Failed to fetch featured products: \\$e');
    }
  }

  @override
  Future<List<SupabaseProductModel>> getRelatedProducts(String productId) async {
    try {
      // Get the product first
      final product = await getProduct(productId);

      // Get products from same category, excluding current product
      final response = await _supabase
          .from(SupabaseConstants.productsTable)
          .select('*')
          .eq('category_id', product.categoryId)
          .neq('id', productId)
          .limit(5)
          .order('rating', ascending: false);

      return response
          .map((json) => SupabaseProductModel.fromJson(json))
          .toList();
    } catch (e) {
      throw DatabaseFailure('Failed to fetch related products: \\$e');
    }
  }

  @override
  Future<void> updateProductStock(String productId, int quantity) async {
    try {
      await _supabase.rpc('decrement_product_stock', params: {
        'product_id': productId,
        'quantity': quantity,
      });
    } catch (e) {
      throw DatabaseFailure('Failed to update product stock: \\$e');
    }
  }
}

