import 'package:flutter/foundation.dart';
import 'package:supabase_flutter/supabase_flutter.dart';
import 'package:riverpod_annotation/riverpod_annotation.dart';
import 'package:supabase_fashion/data/models/user_model.dart';
import 'package:supabase_fashion/core/error/failure.dart';

part 'supabase_auth_provider.g.dart';

@riverpod
class SupabaseAuth extends _$SupabaseAuth {
  late SupabaseClient _supabase;
  UserModel? _currentUser;

  @override
  Future<UserModel?> build() async {
    _supabase = Supabase.instance.client;

    // Check for existing session
    final session = _supabase.auth.currentSession;
    if (session != null) {
      return await _getUserProfile(session.user.id);
    }

    return null;
  }

  Future<UserModel?> _getUserProfile(String userId) async {
    try {
      final response = await _supabase
          .from('users')
          .select('*')
          .eq('id', userId)
          .single();

      return UserModel.fromJson(response);
    } catch (e) {
      // User profile might not exist yet
      return null;
    }
  }

  Future<void> signUpWithEmail({
    required String email,
    required String password,
    required String fullName,
  }) async {
    try {
      state = const AsyncLoading();

      // 1. Sign up with Supabase Auth
      final authResponse = await _supabase.auth.signUp(
        email: email,
        password: password,
      );

      if (authResponse.user == null) {
        throw Failure('Sign up failed');
      }

      // 2. Create user profile
      final user = UserModel(
        id: authResponse.user!.id,
        email: email,
        fullName: fullName,
        createdAt: DateTime.now(),
        updatedAt: DateTime.now(),
      );

      await _supabase
          .from('users')
          .insert(user.toJson());

      state = AsyncData(user);
    } catch (e) {
      state = AsyncError(Failure(e.toString()), StackTrace.current);
      rethrow;
    }
  }

  Future<void> signInWithEmail({
    required String email,
    required String password,
  }) async {
    try {
      state = const AsyncLoading();

      final authResponse = await _supabase.auth.signInWithPassword(
        email: email,
        password: password,
      );

      final user = await _getUserProfile(authResponse.user!.id);

      state = AsyncData(user);
    } catch (e) {
      state = AsyncError(Failure(e.toString()), StackTrace.current);
      rethrow;
    }
  }

  Future<void> signInWithGoogle() async {
    try {
      state = const AsyncLoading();

      await _supabase.auth.signInWithOAuth(
        Provider.google,
        redirectTo: 'io.supabase.flutter://login-callback/',
      );

      // User will be updated via auth state listener
    } catch (e) {
      state = AsyncError(Failure(e.toString()), StackTrace.current);
      rethrow;
    }
  }

  Future<void> signInWithApple() async {
    try {
      state = const AsyncLoading();

      await _supabase.auth.signInWithOAuth(
        Provider.apple,
        redirectTo: 'io.supabase.flutter://login-callback/',
      );
    } catch (e) {
      state = AsyncError(Failure(e.toString()), StackTrace.current);
      rethrow;
    }
  }

  Future<void> signOut() async {
    try {
      await _supabase.auth.signOut();
      state = const AsyncData(null);
    } catch (e) {
      state = AsyncError(Failure(e.toString()), StackTrace.current);
    }
  }

  Future<void> updateProfile(UserModel user) async {
    try {
      state = const AsyncLoading();

      await _supabase
          .from('users')
          .update(user.toJson())
          .eq('id', user.id);

      state = AsyncData(user);
    } catch (e) {
      state = AsyncError(Failure(e.toString()), StackTrace.current);
      rethrow;
    }
  }

  Future<void> resetPassword(String email) async {
    try {
      await _supabase.auth.resetPasswordForEmail(email);
    } catch (e) {
      throw Failure(e.toString());
    }
  }
}

