supabase.realtime_functions.sql:
-- Real-time functions and triggers

-- Function to update current viewers count
CREATE OR REPLACE FUNCTION update_live_viewers_count()
RETURNS TRIGGER AS $$
BEGIN
  IF TG_OP = 'INSERT' THEN
    UPDATE live_shows
    SET current_viewers = current_viewers + 1,
        total_viewers = total_viewers + 1,
        max_viewers = GREATEST(max_viewers, current_viewers + 1)
    WHERE id = NEW.live_show_id;
  ELSIF TG_OP = 'DELETE' THEN
    UPDATE live_shows
    SET current_viewers = current_viewers - 1
    WHERE id = OLD.live_show_id;
  END IF;
  RETURN NEW;
END;
$$ LANGUAGE plpgsql;

-- Create trigger for live viewers
CREATE TRIGGER live_viewers_trigger
AFTER INSERT OR DELETE ON live_viewer_sessions
FOR EACH ROW EXECUTE FUNCTION update_live_viewers_count();

-- Function to send breaking news notifications
CREATE OR REPLACE FUNCTION notify_breaking_news()
RETURNS TRIGGER AS $$
BEGIN
  PERFORM pg_notify(
    'breaking_news',
    json_build_object(
      'id', NEW.id,
      'title', NEW.title,
      'category', NEW.category,
      'severity', NEW.severity,
      'created_at', NEW.created_at
    )::text
  );
  RETURN NEW;
END;
$$ LANGUAGE plpgsql;

-- Create trigger for breaking news
CREATE TRIGGER breaking_news_trigger
AFTER INSERT ON breaking_news
FOR EACH ROW EXECUTE FUNCTION notify_breaking_news();

-- Function for live comment reactions
CREATE OR REPLACE FUNCTION process_live_reaction()
RETURNS TRIGGER AS $$
BEGIN
  -- Update reaction count in live_shows
  UPDATE live_shows
  SET like_count = like_count + 1
  WHERE id = NEW.live_show_id
  AND NEW.reaction_type = 'like';

  -- Send real-time notification
  PERFORM pg_notify(
    'live_reactions',
    json_build_object(
      'live_show_id', NEW.live_show_id,
      'user_id', NEW.user_id,
      'reaction_type', NEW.reaction_type
    )::text
  );

  RETURN NEW;
END;
$$ LANGUAGE plpgsql;

-- Create trigger for live reactions
CREATE TRIGGER live_reaction_trigger
AFTER INSERT ON live_reactions
FOR EACH ROW EXECUTE FUNCTION process_live_reaction();

