import 'package:flutter/material.dart';
import 'package:flutter_riverpod/flutter_riverpod.dart';
import 'package:agora_rtc_engine/agora_rtc_engine.dart';
import 'package:video_player/video_player.dart';
import 'package:chewie/chewie.dart';
import 'package:live_news_channel/core/providers/live_provider.dart';
import 'package:live_news_channel/presentation/widgets/live_chat_widget.dart';

class LiveStreamScreen extends ConsumerStatefulWidget {
  final String liveShowId;

  const LiveStreamScreen({
    super.key,
    required this.liveShowId,
  });

  @override
  ConsumerState<LiveStreamScreen> createState() => _LiveStreamScreenState();
}

class _LiveStreamScreenState extends ConsumerState<LiveStreamScreen> {
  late RtcEngine _agoraEngine;
  VideoPlayerController? _videoController;
  ChewieController? _chewieController;
  bool _isJoined = false;
  bool _isFullScreen = false;
  bool _showChat = true;

  @override
  void initState() {
    super.initState();
    _initAgora();
  }

  Future<void> _initAgora() async {
    // Create Agora engine instance
    _agoraEngine = createAgoraRtcEngine();
    await _agoraEngine.initialize(const RtcEngineContext(
      appId: AppConstants.agoraAppId,
    ));

    // Enable video
    await _agoraEngine.enableVideo();

    // Set up event handlers
    _agoraEngine.registerEventHandler(
      RtcEngineEventHandler(
        onJoinChannelSuccess: (connection, elapsed) {
          setState(() {
            _isJoined = true;
          });
        },
        onUserJoined: (connection, remoteUid, elapsed) {
          // Handle remote user joined
        },
        onUserOffline: (connection, remoteUid, reason) {
          // Handle remote user offline
        },
      ),
    );
  }

  Future<void> _joinChannel() async {
    // Get live show details
    final liveShow = ref.read(liveShowProvider(widget.liveShowId));

    // Join channel
    await _agoraEngine.joinChannel(
      token: liveShow.agoraToken ?? '',
      channelId: liveShow.agoraChannelName ?? '',
      uid: 0, // 0 means Agora assigns a UID
      options: const ChannelMediaOptions(),
    );
  }

  @override
  void dispose() {
    _agoraEngine.leaveChannel();
    _agoraEngine.release();
    _videoController?.dispose();
    _chewieController?.dispose();
    super.dispose();
  }

  @override
  Widget build(BuildContext context) {
    final liveShowAsync = ref.watch(liveShowProvider(widget.liveShowId));

    return Scaffold(
      body: liveShowAsync.when(
        data: (liveShow) {
          return Stack(
            children: [
              // Video Player
              Positioned.fill(
                child: _isJoined
