import 'package:equatable/equatable.dart';
import 'package:live_news_channel/core/constants/enums.dart';

class LiveShowModel extends Equatable {
  final String id;
  final String channelId;
  final String presenterId;
  final String title;
  final String? description;
  final String? thumbnailUrl;
  final String? streamUrl;
  final String? rtmpUrl;
  final String? streamKey;
  final String? agoraChannelName;
  final String? agoraToken;
  final DateTime scheduleTime;
  final DateTime? startTime;
  final DateTime? endTime;
  final LiveStatus status;
  final String category;
  final List<String> tags;
  final int maxViewers;
  final int currentViewers;
  final int peakViewers;
  final int totalViewers;
  final int likeCount;
  final int shareCount;
  final bool isFeatured;
  final bool isExclusive;
  final bool requiresSubscription;
  final String? vodUrl;
  final int? vodDuration;
  final DateTime createdAt;
  final DateTime updatedAt;

  const LiveShowModel({
    required this.id,
    required this.channelId,
    required this.presenterId,
    required this.title,
    this.description,
    this.thumbnailUrl,
    this.streamUrl,
    this.rtmpUrl,
    this.streamKey,
    this.agoraChannelName,
    this.agoraToken,
    required this.scheduleTime,
    this.startTime,
    this.endTime,
    this.status = LiveStatus.upcoming,
    required this.category,
    this.tags = const [],
    this.maxViewers = 0,
    this.currentViewers = 0,
    this.peakViewers = 0,
    this.totalViewers = 0,
    this.likeCount = 0,
    this.shareCount = 0,
    this.isFeatured = false,
    this.isExclusive = false,
    this.requiresSubscription = false,
    this.vodUrl,
    this.vodDuration,
    required this.createdAt,
    required this.updatedAt,
  });

  factory LiveShowModel.fromJson(Map<String, dynamic> json) {
    return LiveShowModel(
      id: json['id'],
      channelId: json['channel_id'],
      presenterId: json['presenter_id'],
      title: json['title'],
      description: json['description'],
      thumbnailUrl: json['thumbnail_url'],
      streamUrl: json['stream_url'],
      rtmpUrl: json['rtmp_url'],
      streamKey: json['stream_key'],
      agoraChannelName: json['agora_channel_name'],
      agoraToken: json['agora_token'],
      scheduleTime: DateTime.parse(json['schedule_time']),
      startTime: json['start_time'] != null
          ? DateTime.parse(json['start_time'])
          : null,
      endTime: json['end_time'] != null
          ? DateTime.parse(json['end_time'])
          : null,
      status: LiveStatus.values.firstWhere(
        (e) => e.name == json['status'],
        orElse: () => LiveStatus.upcoming,
      ),
      category: json['category'],
      tags: List<String>.from(json['tags'] ?? []),
      maxViewers: json['max_viewers'] ?? 0,
      currentViewers: json['current_viewers'] ?? 0,
      peakViewers: json['peak_viewers'] ?? 0,
      totalViewers: json['total_viewers'] ?? 0,
      likeCount: json['like_count'] ?? 0,
      shareCount: json['share_count'] ?? 0,
      isFeatured: json['is_featured'] ?? false,
      isExclusive: json['is_exclusive'] ?? false,
      requiresSubscription: json['requires_subscription'] ?? false,
      vodUrl: json['vod_url'],
      vodDuration: json['vod_duration'],
      createdAt: DateTime.parse(json['created_at']),
      updatedAt: DateTime.parse(json['updated_at']),
    );
  }

  bool get isLive => status == LiveStatus.live;
  bool get isUpcoming => status == LiveStatus.upcoming;
  bool get hasEnded => status == LiveStatus.ended;

  String get timeUntilLive {
    final now = DateTime.now();
    if (isLive) return 'LIVE NOW';

    final difference = scheduleTime.difference(now);

    if (difference.inDays > 0) {
      return 'In \\${difference.inDays}d';
    } else if (difference.inHours > 0) {
      return 'In \\${difference.inHours}h';
    } else if (difference.inMinutes > 0) {
      return 'In \\${difference.inMinutes}m';
    } else {
      return 'Starting soon';
    }
  }

  String get viewerCountFormatted {
    if (currentViewers >= 1000) {
      return '\\${(currentViewers / 1000).toStringAsFixed(1)}K viewers';
    }
    return '\\$currentViewers viewers';
  }

  @override
  List<Object?> get props => [id, status];
}

