      home: const App(),
    );
  }
}

functions.end_to_end_encryption.sql:
-- Function to generate encryption key for conversation
CREATE OR REPLACE FUNCTION generate_conversation_key(conversation_id UUID)
RETURNS TEXT AS $$
DECLARE
  encryption_key TEXT;
BEGIN
  -- Generate a random encryption key
  encryption_key := encode(gen_random_bytes(32), 'base64');

  -- Update conversation with encryption key
  UPDATE conversations 
  SET 
    is_encrypted = true,
    encryption_key = encryption_key,
    updated_at = NOW()
  WHERE id = conversation_id;

  RETURN encryption_key;
END;
$$ LANGUAGE plpgsql;

