services.socket_service.dart:
import 'package:web_socket_channel/web_socket_channel.dart';
import 'package:web_socket_channel/status.dart' as ws_status;
import 'package:chat_app/core/error/failure.dart';

typedef MessageCallback = void Function(Map<String, dynamic>);
typedef ConnectionCallback = void Function(bool);

exception SocketServiceException {
  final String message;
  const SocketServiceException(this.message);
}

class SocketService {
  WebSocketChannel? _channel;
  String? _userId;
  String? _authToken;

  final List<MessageCallback> _messageCallbacks = [];
  final List<ConnectionCallback> _connectionCallbacks = [];

  bool get isConnected => _channel != null;

  Future<void> connect({
    required String url,
    required String userId,
    required String authToken,
  }) async {
    try {
      _userId = userId;
      _authToken = authToken;

      final wsUrl = '\\$url/websocket?user_id=\\$userId&token=\\$authToken';

      _channel = WebSocketChannel.connect(
        Uri.parse(wsUrl),
      );

      // Listen for messages
      _channel!.stream.listen(
        (message) {
          _handleMessage(message);
        },
        onError: (error) {
          _handleError(error);
        },
        onDone: () {
          _handleDisconnection();
        },
      );

      _notifyConnection(true);
    } catch (e) {
      throw SocketServiceException('Failed to connect: \\$e');
    }
  }

  void disconnect() {
    _channel?.sink.close(ws_status.normalClosure);
    _channel = null;
    _notifyConnection(false);
  }

  Future<void> sendMessage({
    required String type,
    required Map<String, dynamic> data,
  }) async {
    if (_channel == null) {
      throw SocketServiceException('Not connected');
    }

    final message = {
      'type': type,
      'data': data,
      'timestamp': DateTime.now().millisecondsSinceEpoch,
      'user_id': _userId,
    };

    try {
      _channel!.sink.add(message);
    } catch (e) {
      throw SocketServiceException('Failed to send message: \\$e');
    }
  }

  void subscribe(MessageCallback callback) {
    _messageCallbacks.add(callback);
  }

  void unsubscribe(MessageCallback callback) {
    _messageCallbacks.remove(callback);
  }

  void onConnectionChange(ConnectionCallback callback) {
    _connectionCallbacks.add(callback);
  }

  void _handleMessage(dynamic rawMessage) {
    try {
      final message = Map<String, dynamic>.from(rawMessage);

      for (final callback in _messageCallbacks) {
        callback(message);
      }
    } catch (e) {
      print('Error handling message: \\$e');
    }
  }

  void _handleError(dynamic error) {
    print('WebSocket error: \\$error');
    disconnect();
  }

  void _handleDisconnection() {
    print('WebSocket disconnected');
    _notifyConnection(false);
  }

  void _notifyConnection(bool isConnected) {
    for (final callback in _connectionCallbacks) {
      callback(isConnected);
    }
  }
}

encryption.e2e_encryption.dart:
import 'package:encrypt/encrypt.dart';
import 'package:crypto/crypto.dart';
import 'dart:convert';
import 'dart:typed_data';

class E2EEncryption {
  final Key _key;
  final IV _iv;

  E2EEncryption.fromKey(String base64Key)
      : _key = Key.fromBase64(base64Key),
        _iv = IV.fromLength(16);

  E2EEncryption.generate()
      : _key = Key.fromSecureRandom(32),
        _iv = IV.fromSecureRandom(16);

  String get keyBase64 => _key.base64;

  String encrypt(String plaintext) {
    final encrypter = Encrypter(AES(_key, mode: AESMode.cbc));
    final encrypted = encrypter.encrypt(plaintext, iv: _iv);
    return encrypted.base64;
  }

  String decrypt(String ciphertext) {
    final encrypter = Encrypter(AES(_key, mode: AESMode.cbc));
    final encrypted = Encrypted.fromBase64(ciphertext);
    return encrypter.decrypt(encrypted, iv: _iv);
  }

  Map<String, dynamic> encryptJson(Map<String, dynamic> data) {
    final jsonString = json.encode(data);
    final encrypted = encrypt(jsonString);

    return {
      'encrypted': true,
      'data': encrypted,
      'iv': _iv.base64,
      'algorithm': 'AES-256-CBC',
    };
  }

  Map<String, dynamic> decryptJson(Map<String, dynamic> encryptedData) {
    if (!encryptedData['encrypted']) {
      return encryptedData;
    }

    final decrypted = decrypt(encryptedData['data']);
    return json.decode(decrypted);
  }

  static String generateKeyPair() {
    final random = Random.secure();
    final bytes = List<int>.generate(32, (_) => random.nextInt(256));
    return base64.encode(bytes);
  }

  static String hashMessage(String message) {
    final bytes = utf8.encode(message);
    final digest = sha256.convert(bytes);
    return digest.toString();
  }
}

