import 'package:equatable/equatable.dart';
import 'package:hive/hive.dart';
import 'package:chat_app/core/constants/message_types.dart';

part 'message_model.g.dart';

@HiveType(typeId: 1)
class MessageModel extends Equatable {
  @HiveField(0)
  final String id;

  @HiveField(1)
  final String conversationId;

  @HiveField(2)
  final String senderId;

  @HiveField(3)
  final String? parentMessageId;

  @HiveField(4)
  final String? content;

  @HiveField(5)
  final MessageType messageType;

  @HiveField(6)
  final List<String> mediaUrls;

  @HiveField(7)
  final String? thumbnailUrl;

  @HiveField(8)
  final String? fileName;

  @HiveField(9)
  final int? fileSize;

  @HiveField(10)
  final String? fileType;

  @HiveField(11)
  final double? latitude;

  @HiveField(12)
  final double? longitude;

  @HiveField(13)
  final String? locationName;

  @HiveField(14)
  final String? contactName;

  @HiveField(15)
  final String? contactPhone;

  @HiveField(16)
  final bool isEdited;

  @HiveField(17)
  final bool isDeleted;

  @HiveField(18)
  final bool deletedForEveryone;

  @HiveField(19)
  final String? encryptionKey;

  @HiveField(20)
  final DateTime createdAt;

  @HiveField(21)
  final DateTime updatedAt;

  @HiveField(22)
  final DateTime? deliveredAt;

  @HiveField(23)
  final DateTime? readAt;

  @HiveField(24)
  final List<MessageReaction> reactions;

  MessageModel({
    required this.id,
    required this.conversationId,
    required this.senderId,
    this.parentMessageId,
    this.content,
    required this.messageType,
    this.mediaUrls = const [],
    this.thumbnailUrl,
    this.fileName,
    this.fileSize,
    this.fileType,
    this.latitude,
    this.longitude,
    this.locationName,
    this.contactName,
    this.contactPhone,
    this.isEdited = false,
    this.isDeleted = false,
    this.deletedForEveryone = false,
    this.encryptionKey,
    required this.createdAt,
    required this.updatedAt,
    this.deliveredAt,
    this.readAt,
    this.reactions = const [],
  });

  factory MessageModel.fromJson(Map<String, dynamic> json) {
    return MessageModel(
      id: json['id'],
      conversationId: json['conversation_id'],
      senderId: json['sender_id'],
      parentMessageId: json['parent_message_id'],
      content: json['content'],
      messageType: MessageType.values.firstWhere(
        (e) => e.name == (json['message_type'] as String).toLowerCase(),
        orElse: () => MessageType.text,
      ),
      mediaUrls: List<String>.from(json['media_urls'] ?? []),
      thumbnailUrl: json['thumbnail_url'],
      fileName: json['file_name'],
      fileSize: json['file_size'],
      fileType: json['file_type'],
      latitude: json['latitude'],
      longitude: json['longitude'],
      locationName: json['location_name'],
      contactName: json['contact_name'],
      contactPhone: json['contact_phone'],
      isEdited: json['is_edited'] ?? false,
      isDeleted: json['is_deleted'] ?? false,
      deletedForEveryone: json['deleted_for_everyone'] ?? false,
      encryptionKey: json['encryption_key'],
      createdAt: DateTime.parse(json['created_at']),
      updatedAt: DateTime.parse(json['updated_at']),
      deliveredAt: json['delivered_at'] != null 
          ? DateTime.parse(json['delivered_at'])
          : null,
      readAt: json['read_at'] != null 
          ? DateTime.parse(json['read_at'])
          : null,
      reactions: List<MessageReaction>.from(
        (json['reactions'] ?? []).map(
          (r) => MessageReaction.fromJson(r),
        ),
      ),
    );
  }

  Map<String, dynamic> toJson() {
    return {
      'id': id,
      'conversation_id': conversationId,
      'sender_id': senderId,
      'parent_message_id': parentMessageId,
      'content': content,
      'message_type': messageType.name,
      'media_urls': mediaUrls,
      'thumbnail_url': thumbnailUrl,
      'file_name': fileName,
      'file_size': fileSize,
      'file_type': fileType,
      'latitude': latitude,
      'longitude': longitude,
      'location_name': locationName,
      'contact_name': contactName,
      'contact_phone': contactPhone,
      'is_edited': isEdited,
      'is_deleted': isDeleted,
      'deleted_for_everyone': deletedForEveryone,
      'encryption_key': encryptionKey,
      'created_at': createdAt.toIso8601String(),
      'updated_at': updatedAt.toIso8601String(),
      'delivered_at': deliveredAt?.toIso8601String(),
      'read_at': readAt?.toIso8601String(),
      'reactions': reactions.map((r) => r.toJson()).toList(),
    };
  }

  MessageModel copyWith({
    String? id,
    String? conversationId,
    String? senderId,
    String? parentMessageId,
    String? content,
    MessageType? messageType,
    List<String>? mediaUrls,
    String? thumbnailUrl,
    String? fileName,
    int? fileSize,
    String? fileType,
    double? latitude,
    double? longitude,
    String? locationName,
    String? contactName,
    String? contactPhone,
    bool? isEdited,
    bool? isDeleted,
    bool? deletedForEveryone,
    String? encryptionKey,
    DateTime? createdAt,
    DateTime? updatedAt,
    DateTime? deliveredAt,
    DateTime? readAt,
    List<MessageReaction>? reactions,
  }) {
    return MessageModel(
      id: id ?? this.id,
      conversationId: conversationId ?? this.conversationId,
      senderId: senderId ?? this.senderId,
      parentMessageId: parentMessageId ?? this.parentMessageId,
      content: content ?? this.content,
      messageType: messageType ?? this.messageType,
      mediaUrls: mediaUrls ?? this.mediaUrls,
      thumbnailUrl: thumbnailUrl ?? this.thumbnailUrl,
      fileName: fileName ?? this.fileName,
      fileSize: fileSize ?? this.fileSize,
      fileType: fileType ?? this.fileType,
      latitude: latitude ?? this.latitude,
      longitude: longitude ?? this.longitude,
      locationName: locationName ?? this.locationName,
      contactName: contactName ?? this.contactName,
      contactPhone: contactPhone ?? this.contactPhone,
      isEdited: isEdited ?? this.isEdited,
      isDeleted: isDeleted ?? this.isDeleted,
      deletedForEveryone: deletedForEveryone ?? this.deletedForEveryone,
      encryptionKey: encryptionKey ?? this.encryptionKey,
      createdAt: createdAt ?? this.createdAt,
      updatedAt: updatedAt ?? DateTime.now(),
      deliveredAt: deliveredAt ?? this.deliveredAt,
      readAt: readAt ?? this.readAt,
      reactions: reactions ?? this.reactions,
    );
  }

  bool get isMediaMessage => [
    MessageType.image,
    MessageType.video,
    MessageType.audio,
    MessageType.file,
  ].contains(messageType);

  bool get isTextMessage => messageType == MessageType.text;

  bool get isSystemMessage => messageType == MessageType.system;

  bool get hasReactions => reactions.isNotEmpty;

  bool get isSent => deliveredAt != null;

  bool get isRead => readAt != null;

  String get previewText {
    if (isDeleted) {
      return 'This message was deleted';
    }

    switch (messageType) {
      case MessageType.text:
        return content ?? '';
      case MessageType.image:
        return '📷 Image';
      case MessageType.video:
        return '🎥 Video';
      case MessageType.audio:
        return '🎵 Audio';
      case MessageType.file:
        return '📄 \\${fileName ?? "File"}';
      case MessageType.location:
        return '📍 Location';
      case MessageType.contact:
        return '👤 \\${contactName ?? "Contact"}';
      case MessageType.system:
        return '⚙️ System message';
    }
  }

  @override
  List<Object?> get props => [id];
}

@HiveType(typeId: 2)
class MessageReaction extends Equatable {
  @HiveField(0)
