blocs.chat_bloc.dart:
import 'dart:async';
import 'package:bloc/bloc.dart';
import 'package:chat_app/data/models/message_model.dart';
import 'package:chat_app/data/models/conversation_model.dart';
import 'package:chat_app/data/repositories/chat_repository.dart';
import 'package:chat_app/core/services/socket_service.dart';
import 'package:equatable/equatable.dart';

part 'chat_event.dart';
part 'chat_state.dart';

class ChatBloc extends Bloc<ChatEvent, ChatState> {
  final ChatRepository _chatRepository;
  final SocketService _socketService;

  StreamSubscription? _messagesSubscription;
  StreamSubscription? _typingSubscription;
  StreamSubscription? _userStatusSubscription;

  ChatBloc({
    required ChatRepository chatRepository,
    required SocketService socketService,
  })  : _chatRepository = chatRepository,
        _socketService = socketService,
        super(const ChatInitial()) {

    on<LoadConversations>(_onLoadConversations);
    on<LoadMessages>(_onLoadMessages);
    on<SendMessage>(_onSendMessage);
    on<ReceiveMessage>(_onReceiveMessage);
    on<UpdateMessage>(_onUpdateMessage);
    on<DeleteMessage>(_onDeleteMessage);
    on<ReactToMessage>(_onReactToMessage);
    on<StartTyping>(_onStartTyping);
    on<StopTyping>(_onStopTyping);
    on<UpdateUserStatus>(_onUpdateUserStatus);
    on<MarkAsRead>(_onMarkAsRead);
    on<UploadFile>(_onUploadFile);
    on<StartCall>(_onStartCall);
    on<EndCall>(_onEndCall);

    _setupSocketListeners();
  }

  void _setupSocketListeners() {
    _socketService.subscribe((message) {
      final type = message['type'] as String;
      final data = message['data'] as Map<String, dynamic>;

      switch (type) {
        case 'new_message':
          add(ReceiveMessage(MessageModel.fromJson(data)));
          break;
        case 'message_updated':
          add(UpdateMessage(MessageModel.fromJson(data)));
          break;
        case 'message_deleted':
          add(DeleteMessage(data['message_id'], data['deleted_for_everyone']));
          break;
        case 'typing_started':
          add(StartTyping(data['conversation_id'], data['user_id']));
          break;
        case 'typing_stopped':
          add(StopTyping(data['conversation_id'], data['user_id']));
          break;
        case 'user_status_updated':
          add(UpdateUserStatus(data['user_id'], data['status']));
          break;
        case 'call_started':
          // Handle incoming call
          break;
        case 'call_ended':
          // Handle call ended
          break;
      }
    });
  }

  Future<void> _onLoadConversations(
    LoadConversations event,
    Emitter<ChatState> emit,
  ) async {
    emit(const ConversationsLoading());

    try {
      final conversations = await _chatRepository.getConversations();
      emit(ConversationsLoaded(conversations));
    } catch (e) {
      emit(ChatError('Failed to load conversations: \\$e'));
    }
  }

  Future<void> _onLoadMessages(
    LoadMessages event,
    Emitter<ChatState> emit,
  ) async {
    if (state is MessagesLoaded) {
      final currentState = state as MessagesLoaded;
      if (currentState.conversationId == event.conversationId && 
          !event.forceRefresh) {
        return;
      }
    }

    emit(const MessagesLoading());

    try {
      final messages = await _chatRepository.getMessages(
        conversationId: event.conversationId,
        limit: event.limit,
        before: event.before,
      );

      emit(MessagesLoaded(
        conversationId: event.conversationId,
        messages: messages,
        hasMore: messages.length >= event.limit,
      ));
    } catch (e) {
      emit(ChatError('Failed to load messages: \\$e'));
    }
  }

  Future<void> _onSendMessage(
    SendMessage event,
    Emitter<ChatState> emit,
  ) async {
    try {
      // Add message to local state immediately
      if (state is MessagesLoaded) {
        final currentState = state as MessagesLoaded;
        final updatedMessages = [event.message, ...currentState.messages];

        emit(currentState.copyWith(messages: updatedMessages));
      }

      // Send to server
      await _chatRepository.sendMessage(event.message);

      // Notify via socket
      await _socketService.sendMessage(
        type: 'send_message',
        data: event.message.toJson(),
      );
    } catch (e) {
      emit(ChatError('Failed to send message: \\$e'));
    }
  }

  Future<void> _onReceiveMessage(
    ReceiveMessage event,
    Emitter<ChatState> emit,
  ) async {
    if (state is MessagesLoaded) {
      final currentState = state as MessagesLoaded;

      // Check if message already exists
      final exists = currentState.messages.any((m) => m.id == event.message.id);
      if (exists) return;

      final updatedMessages = [event.message, ...currentState.messages];
      emit(currentState.copyWith(messages: updatedMessages));
    }
  }

  Future<void> _onUpdateMessage(
    UpdateMessage event,
    Emitter<ChatState> emit,
  ) async {
    if (state is MessagesLoaded) {
      final currentState = state as MessagesLoaded;
      final updatedMessages = currentState.messages.map((message) {
        return message.id == event.message.id ? event.message : message;
      }).toList();

      emit(currentState.copyWith(messages: updatedMessages));
    }
  }

  Future<void> _onDeleteMessage(
    DeleteMessage event,
    Emitter<ChatState> emit,
  ) async {
    if (state is MessagesLoaded) {
      final currentState = state as MessagesLoaded;

      final updatedMessages = currentState.messages.map((message) {
        if (message.id == event.messageId) {
          return message.copyWith(
            isDeleted: true,
            deletedForEveryone: event.deletedForEveryone,
          );
        }
        return message;
      }).toList();

      emit(currentState.copyWith(messages: updatedMessages));
    }
  }

  Future<void> _onReactToMessage(
    ReactToMessage event,
    Emitter<ChatState> emit,
  ) async {
    try {
      await _chatRepository.addReaction(
        messageId: event.messageId,
        emoji: event.emoji,
      );

      // Notify via socket
      await _socketService.sendMessage(
        type: 'react_to_message',
        data: {
          'message_id': event.messageId,
          'emoji': event.emoji,
        },
      );
    } catch (e) {
      emit(ChatError('Failed to react to message: \\$e'));
    }
  }

  void _onStartTyping(
    StartTyping event,
    Emitter<ChatState> emit,
  ) {
    if (state is MessagesLoaded) {
      final currentState = state as MessagesLoaded;
      final typingUsers = {...currentState.typingUsers, event.userId};
      emit(currentState.copyWith(typingUsers: typingUsers));
    }
  }

  void _onStopTyping(
    StopTyping event,
    Emitter<ChatState> emit,
  ) {
    if (state is MessagesLoaded) {
      final currentState = state as MessagesLoaded;
      final typingUsers = {...currentState.typingUsers};
      typingUsers.remove(event.userId);
      emit(currentState.copyWith(typingUsers: typingUsers));
    }
  }

  void _onUpdateUserStatus(
    UpdateUserStatus event,
    Emitter<ChatState> emit,
  ) {
    // Update user status in conversations
    if (state is ConversationsLoaded) {
      final currentState = state as ConversationsLoaded;
      final updatedConversations = currentState.conversations.map((conv) {
        // Update participant status if needed
        return conv;
      }).toList();

      emit(currentState.copyWith(conversations: updatedConversations));
    }
  }

  Future<void> _onMarkAsRead(
    MarkAsRead event,
    Emitter<ChatState> emit,
  ) async {
    try {
      await _chatRepository.markAsRead(event.conversationId);
    } catch (e) {
      emit(ChatError('Failed to mark as read: \\$e'));
    }
  }

  Future<void> _onUploadFile(
    UploadFile event,
    Emitter<ChatState> emit,
  ) async {
    emit(const FileUploading());

    try {
      final fileUrl = await _chatRepository.uploadFile(
        filePath: event.filePath,
        fileName: event.fileName,
        fileType: event.fileType,
      );

      emit(FileUploaded(fileUrl));
    } catch (e) {
      emit(ChatError('Failed to upload file: \\$e'));
    }
  }

  Future<void> _onStartCall(
    StartCall event,
    Emitter<ChatState> emit,
  ) async {
    try {
      await _socketService.sendMessage(
        type: 'start_call',
        data: {
          'conversation_id': event.conversationId,
          'call_type': event.callType.name,
        },
      );
    } catch (e) {
      emit(ChatError('Failed to start call: \\$e'));
    }
  }

  Future<void> _onEndCall(
    EndCall event,
    Emitter<ChatState> emit,
  ) async {
    try {
      await _socketService.sendMessage(
        type: 'end_call',
        data: {
          'call_id': event.callId,
          'duration': event.duration,
        },
      );
    } catch (e) {
      emit(ChatError('Failed to end call: \\$e'));
    }
  }

  @override
  Future<void> close() {
    _messagesSubscription?.cancel();
    _typingSubscription?.cancel();
    _userStatusSubscription?.cancel();
    _socketService.disconnect();
    return super.close();
  }
}

chat.chat_bubble.dart:
import 'package:flutter/material.dart';
import 'package:cached_network_image/cached_network_image.dart';
import 'package:chat_app/data/models/message_model.dart';
import 'package:chat_app/core/constants/message_types.dart';
import 'package:chat_app/core/utils/formatters.dart';

class ChatBubble extends StatelessWidget {
  final MessageModel message;
  final bool isOwnMessage;
  final bool showAvatar;
  final bool showTime;
  final VoidCallback? onTap;
  final VoidCallback? onLongPress;
  final VoidCallback? onReply;
  final VoidCallback? onReact;

  const ChatBubble({
    super.key,
    required this.message,
    required this.isOwnMessage,
    this.showAvatar = true,
    this.showTime = true,
    this.onTap,
    this.onLongPress,
    this.onReply,
    this.onReact,
  });

  @override
  Widget build(BuildContext context) {
    final theme = Theme.of(context);
    final colorScheme = theme.colorScheme;

    if (message.isDeleted) {
      return _buildDeletedMessage(theme);
    }

    if (message.isSystemMessage) {
      return _buildSystemMessage(theme);
    }

    return Padding(
      padding: const EdgeInsets.symmetric(vertical: 2, horizontal: 8),
      child: Row(
        crossAxisAlignment: CrossAxisAlignment.end,
        mainAxisAlignment: isOwnMessage 
            ? MainAxisAlignment.end 
            : MainAxisAlignment.start,
        children: [
          if (!isOwnMessage && showAvatar)
            Padding(
              padding: const EdgeInsets.only(right: 8),
              child: CircleAvatar(
                radius: 16,
                backgroundImage: CachedNetworkImageProvider(
                  message.senderId, // This should be avatar URL
                ),
              ),
            ),

          Flexible(
            child: Column(
              crossAxisAlignment: isOwnMessage 
                  ? CrossAxisAlignment.end 
                  : CrossAxisAlignment.start,
              children: [
                Container(
                  constraints: BoxConstraints(
                    maxWidth: MediaQuery.of(context).size.width * 0.7,
                  ),
                  padding: const EdgeInsets.symmetric(
                    vertical: 10,
                    horizontal: 14,
                  ),
                  decoration: BoxDecoration(
                    color: isOwnMessage
                        ? colorScheme.primary
                        : colorScheme.surfaceVariant,
                    borderRadius: BorderRadius.only(
                      topLeft: const Radius.circular(16),
                      topRight: const Radius.circular(16),
                      bottomLeft: isOwnMessage 
                          ? const Radius.circular(16)
                          : const Radius.circular(4),
                      bottomRight: isOwnMessage 
                          ? const Radius.circular(4)
                          : const Radius.circular(16),
                    ),
                  ),
                  child: Column(
                    crossAxisAlignment: CrossAxisAlignment.start,
                    children: [
                      if (message.parentMessageId != null)
                        _buildReplyPreview(theme),

                      _buildMessageContent(theme),

                      if (message.hasReactions)
                        _buildReactions(theme),
                    ],
                  ),
                ),

                if (showTime)
                  Padding(
                    padding: const EdgeInsets.only(top: 4),
                    child: Row(
                      mainAxisSize: MainAxisSize.min,
                      children: [
                        Text(
                          Formatters.formatTime(message.createdAt),
                          style: theme.textTheme.bodySmall?.copyWith(
                            color: theme.colorScheme.onSurface.withOpacity(0.5),
                          ),
                        ),

                        if (isOwnMessage) ...[
                          const SizedBox(width: 4),
                          Icon(
                            message.isRead ? Icons.done_all : Icons.done,
                            size: 12,
                            color: message.isRead 
                                ? Colors.blue 
                                : theme.colorScheme.onSurface.withOpacity(0.5),
                          ),
                        ],
                      ],
                    ),
                  ),
              ],
            ),
          ),

          if (isOwnMessage && showAvatar)
            Padding(
              padding: const EdgeInsets.only(left: 8),
              child: CircleAvatar(
                radius: 16,
                backgroundColor: colorScheme.primaryContainer,
                child: Icon(
                  Icons.person,
                  size: 18,
                  color: colorScheme.onPrimaryContainer,
                ),
              ),
            ),
        ],
      ),
    );
  }

  Widget _buildDeletedMessage(ThemeData theme) {
    return Padding(
      padding: const EdgeInsets.symmetric(vertical: 8, horizontal: 16),
      child: Center(
        child: Text(
          'This message was deleted',
          style: theme.textTheme.bodyMedium?.copyWith(
            color: theme.colorScheme.onSurface.withOpacity(0.5),
            fontStyle: FontStyle.italic,
          ),
        ),
      ),
    );
  }

  Widget _buildSystemMessage(ThemeData theme) {
    return Padding(
      padding: const EdgeInsets.symmetric(vertical: 8, horizontal: 16),
      child: Center(
        child: Container(
          padding: const EdgeInsets.symmetric(horizontal: 12, vertical: 6),
          decoration: BoxDecoration(
            color: theme.colorScheme.surfaceVariant,
            borderRadius: BorderRadius.circular(12),
          ),
          child: Text(
            message.content ?? 'System message',
            style: theme.textTheme.bodySmall?.copyWith(
              color: theme.colorScheme.onSurfaceVariant,
            ),
          ),
        ),
      ),
    );
  }

  Widget _buildReplyPreview(ThemeData theme) {
    return Container(
      padding: const EdgeInsets.all(8),
      margin: const EdgeInsets.only(bottom: 8),
      decoration: BoxDecoration(
        color: theme.colorScheme.surface.withOpacity(0.5),
        borderRadius: BorderRadius.circular(8),
        border: Border.all(
          color: theme.colorScheme.outline.withOpacity(0.3),
        ),
      ),
      child: Row(
        children: [
          Container(
            width: 3,
            height: 24,
            color: theme.colorScheme.primary,
          ),
          const SizedBox(width: 8),
          Expanded(
            child: Column(
              crossAxisAlignment: CrossAxisAlignment.start,
              children: [
                Text(
                  'Replying to You',
                  style: theme.textTheme.labelSmall?.copyWith(
                    color: theme.colorScheme.primary,
                  ),
                ),
                Text(
                  'Preview of replied message...',
                  style: theme.textTheme.bodySmall,
                  maxLines: 1,
                  overflow: TextOverflow.ellipsis,
                ),
              ],
            ),
          ),
        ],
      ),
    );
  }

  Widget _buildMessageContent(ThemeData theme) {
    switch (message.messageType) {
      case MessageType.text:
        return SelectableText(
          message.content ?? '',
          style: theme.textTheme.bodyLarge?.copyWith(
            color: isOwnMessage
                ? theme.colorScheme.onPrimary
                : theme.colorScheme.onSurface,
          ),
        );

      case MessageType.image:
        return _buildImageMessage(theme);

      case MessageType.video:
        return _buildVideoMessage(theme);

      case MessageType.audio:
        return _buildAudioMessage(theme);

      case MessageType.file:
        return _buildFileMessage(theme);

      case MessageType.location:
        return _buildLocationMessage(theme);

      case MessageType.contact:
        return _buildContactMessage(theme);

      default:
        return const SizedBox.shrink();
    }
  }

  Widget _buildImageMessage(ThemeData theme) {
    return GestureDetector(
      onTap: () {
        // Open image viewer
      },
      child: ClipRRect(
        borderRadius: BorderRadius.circular(8),
        child: CachedNetworkImage(
          imageUrl: message.mediaUrls.first,
          placeholder: (context, url) => Container(
            width: 200,
            height: 200,
            color: theme.colorScheme.surfaceVariant,
            child: const Center(
              child: CircularProgressIndicator(),
            ),
          ),
          errorWidget: (context, url, error) => Container(
            width: 200,
            height: 200,
            color: theme.colorScheme.errorContainer,
            child: const Center(
              child: Icon(Icons.broken_image),
            ),
          ),
        ),
      ),
    );
  }

  Widget _buildVideoMessage(ThemeData theme) {
    return Container(
      width: 200,
      height: 150,
      decoration: BoxDecoration(
        color: theme.colorScheme.surfaceVariant,
        borderRadius: BorderRadius.circular(8),
      ),
      child: Stack(
        fit: StackFit.expand,
        children: [
          if (message.thumbnailUrl != null)
            CachedNetworkImage(
              imageUrl: message.thumbnailUrl!,
              fit: BoxFit.cover,
            ),

          Center(
            child: Container(
              padding: const EdgeInsets.all(12),
              decoration: const BoxDecoration(
                color: Colors.black54,
                shape: BoxShape.circle,
              ),
              child: const Icon(
                Icons.play_arrow,
                color: Colors.white,
                size: 30,
              ),
            ),
          ),
        ],
      ),
    );
  }

  Widget _buildAudioMessage(ThemeData theme) {
    return Container(
      padding: const EdgeInsets.all(12),
      decoration: BoxDecoration(
        color: theme.colorScheme.surfaceVariant,
        borderRadius: BorderRadius.circular(8),
      ),
      child: Row(
        children: [
          const Icon(Icons.audiotrack, size: 24),
          const SizedBox(width: 12),
          Expanded(
            child: Column(
              crossAxisAlignment: CrossAxisAlignment.start,
              children: [
                Text(
                  'Audio Message',
                  style: theme.textTheme.bodyMedium,
                ),
                if (message.fileSize != null)
                  Text(
                    '${(message.fileSize! / 1024).toStringAsFixed(1)} KB',
                    style: theme.textTheme.bodySmall,
                  ),
              ],
            ),
          ),
          const Icon(Icons.play_circle_filled, size: 32),
        ],
      ),
    );
  }

  Widget _buildFileMessage(ThemeData theme) {
    return Container(
      padding: const EdgeInsets.all(12),
      decoration: BoxDecoration(
        color: theme.colorScheme.surfaceVariant,
        borderRadius: BorderRadius.circular(8),
      ),
      child: Row(
        children: [
          const Icon(Icons.insert_drive_file, size: 32),
          const SizedBox(width: 12),
          Expanded(
            child: Column(
              crossAxisAlignment: CrossAxisAlignment.start,
              children: [
                Text(
                  message.fileName ?? 'File',
                  style: theme.textTheme.bodyMedium,
                  maxLines: 1,
                  overflow: TextOverflow.ellipsis,
                ),
                if (message.fileSize != null)
                  Text(
                    '${(message.fileSize! / 1024).toStringAsFixed(1)} KB',
                    style: theme.textTheme.bodySmall,
                  ),
              ],
            ),
          ),
          const Icon(Icons.download, size: 24),
        ],
      ),
    );
  }

  Widget _buildLocationMessage(ThemeData theme) {
    return Container(
      width: 200,
      height: 150,
      decoration: BoxDecoration(
        color: theme.colorScheme.surfaceVariant,
        borderRadius: BorderRadius.circular(8),
      ),
      child: Column(
        children: [
          Expanded(
            child: const Icon(
              Icons.location_on,
              size: 48,
              color: Colors.red,
            ),
          ),
          Padding(
            padding: const EdgeInsets.all(8),
            child: Text(
              message.locationName ?? 'Location',
              style: theme.textTheme.bodyMedium,
              maxLines: 1,
              overflow: TextOverflow.ellipsis,
            ),
          ),
        ],
      ),
    );
  }

  Widget _buildContactMessage(ThemeData theme) {
    return Container(
      padding: const EdgeInsets.all(12),
      decoration: BoxDecoration(
        color: theme.colorScheme.surfaceVariant,
        borderRadius: BorderRadius.circular(8),
      ),
      child: Row(
        children: [
          const CircleAvatar(
            radius: 24,
            child: Icon(Icons.person, size: 28),
          ),
          const SizedBox(width: 12),
          Expanded(
            child: Column(
              crossAxisAlignment: CrossAxisAlignment.start,
              children: [
                Text(
                  message.contactName ?? 'Contact',
                  style: theme.textTheme.bodyMedium,
                ),
                if (message.contactPhone != null)
                  Text(
                    message.contactPhone!,
                    style: theme.textTheme.bodySmall,
                  ),
              ],
            ),
          ),
          const Icon(Icons.person_add, size: 24),
        ],
      ),
    );
  }

  Widget _buildReactions(ThemeData theme) {
    return Wrap(
      spacing: 4,
      runSpacing: 4,
      children: message.reactions.map((reaction) {
        return Container(
          padding: const EdgeInsets.symmetric(horizontal: 6, vertical: 2),
          decoration: BoxDecoration(
            color: theme.colorScheme.surface,
            borderRadius: BorderRadius.circular(12),
            border: Border.all(
              color: theme.colorScheme.outline.withOpacity(0.3),
            ),
          ),
          child: Row(
            mainAxisSize: MainAxisSize.min,
            children: [
              Text(reaction.emoji),
              const SizedBox(width: 2),
              if (message.reactions.length > 1)
                Text(
                  '${message.reactions.length}',
                  style: theme.textTheme.bodySmall,
                ),
            ],
          ),
        );
      }).toList(),
    );
  }
}

